                        function addNumbers(type,val_id)
                        {
                            if(type=="debit"){
                                splitedValue = val_id.split("_");
                                //alert(splitedValue[2]);
                                var valueOfDebitIdValue = $("#"+val_id).val();

                                var valueOfCreditIdValue = $("#credit_"+splitedValue[1]+"_"+splitedValue[2]).val();
                                if(splitedValue[2]=="ASSET" || splitedValue[2]=="EXPENSE") {
                                    balanceAmount = valueOfDebitIdValue - valueOfCreditIdValue;
                                }
                                else { balanceAmount = valueOfCreditIdValue - valueOfDebitIdValue; }
                                $("#balance_"+splitedValue[1]).attr('value',balanceAmount.toFixed(2));
                                //alert(balanceAmount);
                            }
                            if(type=="credit"){
                                splitedValue = val_id.split("_");
                                var valueOfCreditIdValue = $("#"+val_id).val();
                                var valueOfDebitIdValue = $("#debit_"+splitedValue[1]+"_"+splitedValue[2]).val();
                                //balanceAmount = valueOfDebitIdValue - valueOfCreditIdValue;
                                if(splitedValue[2]=="ASSET" || splitedValue[2]=="EXPENSE") {
                                    balanceAmount = valueOfDebitIdValue - valueOfCreditIdValue;
                                }
